// jquery-selectors.js

$(document).ready(function() {
    // jQuery Selectors - Unleashing the Magic
    $('h1').css('color', '#3498db'); // Selecting by element name
    $('.highlight').fadeOut(2000); // Selecting by class
    $('ul li:selected').css('background-color', '#2ecc71'); // Selecting by :selected
  });
  